css:
.invoice-container {
  background-color: white;
  border-radius: 10px;
  box-shadow: 0 0 20px rgba(0, 0, 0, 0.1);
  padding: 40px;
  margin: 20px auto;
  max-width: 900px;
}
.invoice-header {
  border-bottom: 2px solid #e9ecef;
  margin-bottom: 30px;
  padding-bottom: 20px;
}
.invoice-title {
  color: #0d6efd;
  font-weight: bold;
}
.tenant-info {
  background-color: #f8f9fa;
  border-radius: 8px;
  padding: 20px;
}
.shop-table {
  margin-top: 30px;
}
.shop-table th {
  background-color: #f1f8ff;
}
.total-section {
  margin-top: 30px;
  border-top: 1px solid #dee2e6;
  padding-top: 20px;
}
.status-badge {
  font-size: 12px;
  padding: 6px 6px;
}
.invoice-footer {
  margin-top: 40px;
  border-top: 1px solid #dee2e6;
  padding-top: 20px;
  text-align: center;
  color: #6c757d;
  font-size: 14px;
}

   .bg-success{
    background-color:#198754;
    color:white;
}
 .bg-danger{
    background-color:#dc3545;
    color:white;
}
